mapper.select:
SELECT MOVIE.MOVIE_CODE, TITLE , (SELECT COUNT(*) FROM HEARTMOVIE WHERE MOVIE_CODE=movie.movie_code) as "heart_cnt" , OPEN_DATE ,MOVIE_TIME, HEARTMOVIE.ID FROM movie LEFT JOIN heartmovie ON movie.movie_code=heartmovie.movie_code
AND heartmovie.id is null
AND heartmovie.id=#{memId}

mapper.update:
UPDATE movie
SET title=#{title}, open_date=#{openDate}, movie_time=#{movieTime}
WHERE movie_code=#{movieCode}
